	/**
	 * 
	 * [UC0711] - Emitir Ordem de Servico Seletiva Relatorio Ordem Seletiva
	 * Analitico Pesquisa
	 * 
	 * @author Hugo Amorim
	 * @date 03/03/2010
	 */
	public RelatorioEmitirOrdemServicoSeletivaAnaliticoBean pesquisarDadosRelatorioRelatorioEmitirOsSeletivaAnalitico(
			Integer idImovel,
			Collection<RelatorioEmitirOrdemServicoSeletivaAnaliticoSubrelatorioBean> colecaoSubcategorias,
			SistemaParametro sistemaParametro) {

		RelatorioEmitirOrdemServicoSeletivaAnaliticoBean retorno = new RelatorioEmitirOrdemServicoSeletivaAnaliticoBean();

		try {

			// PESQUISA IMOVEL
			FiltroImovel filtroImovel = new FiltroImovel();

			filtroImovel.adicionarParametro(new ParametroSimples(
					FiltroImovel.ID, idImovel));

			filtroImovel
					.adicionarCaminhoParaCarregamentoEntidade(FiltroImovel.LOCALIDADE);
			filtroImovel
					.adicionarCaminhoParaCarregamentoEntidade(FiltroImovel.LOCALIDADE_UNIDADE_NEGOCIO);
			filtroImovel
					.adicionarCaminhoParaCarregamentoEntidade(FiltroImovel.SETOR_COMERCIAL);
			filtroImovel
					.adicionarCaminhoParaCarregamentoEntidade(FiltroImovel.QUADRA);
			filtroImovel
					.adicionarCaminhoParaCarregamentoEntidade(FiltroImovel.LIGACAO_AGUA_HIDROMETRO_INSTALACAO_HISTORICO);
			filtroImovel
					.adicionarCaminhoParaCarregamentoEntidade(FiltroImovel.LIGACAO_ESGOTO_SITUACAO);
			filtroImovel
					.adicionarCaminhoParaCarregamentoEntidade(FiltroImovel.LIGACAO_AGUA_SITUACAO);

			Collection colecaoImovel = getControladorUtil().pesquisar(
					filtroImovel, Imovel.class.getName());

			Imovel imovel = (Imovel) Util.retonarObjetoDeColecao(colecaoImovel);

			// SE ENCONTROU O IMOVEL PESQUISA OUTROS DADOS
			// CASO CONTRARIO ATRIBUI NULL E NÃƒO ADICIONA O
			// BEAN NA COLEÃ‡ÃƒO
			if (imovel != null) {

				retorno.setMatricula(imovel.getMatriculaFormatada());
				retorno.setInscricao(imovel.getInscricaoFormatada());
				retorno.setLocalidade(imovel.getLocalidade().getDescricao());
				retorno.setUnidadeResponsavel(imovel.getLocalidade()
						.getUnidadeNegocio().getNome());
				retorno.setColecaoCategorias(new JRBeanCollectionDataSource(
						colecaoSubcategorias));

				// OBTEM CLIENTE USUARIO
				Cliente cliente = getControladorImovel()
						.pesquisarClienteUsuarioImovel(imovel.getId());
				retorno.setCliente(cliente.getNome());

				// OBTEM ENDERECO
				String endereco = getControladorEndereco().pesquisarEndereco(
						imovel.getId());
				retorno.setEndereco(endereco);

				// OBTEM NUMERO DO HIDROMETRO
				String numeroHidrometro = getControladorMicromedicao()
						.pesquisarNumeroHidrometro(imovel.getId());

				if (numeroHidrometro != null) {
					retorno.setNumeroHidrometro(numeroHidrometro);
				}

				// OBTEM ULTIMOS 6 CONSUMOS DO IMOVEL
				Integer anoMes = sistemaParametro.getAnoMesFaturamento();

				Integer anoMes1 = Util.subtraiAteSeisMesesAnoMesReferencia(
						anoMes, 1);
				Integer anoMes2 = Util.subtraiAteSeisMesesAnoMesReferencia(
						anoMes, 2);
				Integer anoMes3 = Util.subtraiAteSeisMesesAnoMesReferencia(
						anoMes, 3);
				Integer anoMes4 = Util.subtraiAteSeisMesesAnoMesReferencia(
						anoMes, 4);
				Integer anoMes5 = Util.subtraiAteSeisMesesAnoMesReferencia(
						anoMes, 5);
				Integer anoMes6 = Util.subtraiAteSeisMesesAnoMesReferencia(
						anoMes, 6);

				Integer consumo1 = getControladorMicromedicao()
						.pesquisarConsumoFaturaMes(anoMes1,
								LigacaoTipo.LIGACAO_AGUA, imovel.getId());
				Integer consumo2 = getControladorMicromedicao()
						.pesquisarConsumoFaturaMes(anoMes2,
								LigacaoTipo.LIGACAO_AGUA, imovel.getId());
				Integer consumo3 = getControladorMicromedicao()
						.pesquisarConsumoFaturaMes(anoMes3,
								LigacaoTipo.LIGACAO_AGUA, imovel.getId());
				Integer consumo4 = getControladorMicromedicao()
						.pesquisarConsumoFaturaMes(anoMes4,
								LigacaoTipo.LIGACAO_AGUA, imovel.getId());
				Integer consumo5 = getControladorMicromedicao()
						.pesquisarConsumoFaturaMes(anoMes5,
								LigacaoTipo.LIGACAO_AGUA, imovel.getId());
				Integer consumo6 = getControladorMicromedicao()
						.pesquisarConsumoFaturaMes(anoMes6,
								LigacaoTipo.LIGACAO_AGUA, imovel.getId());

				retorno.setMesAno1(Util.formatarAnoMesParaMesAno(anoMes1));
				retorno.setConsumo1(consumo1 != null ? consumo1.toString()
						: "0");
				retorno.setMesAno2(Util.formatarAnoMesParaMesAno(anoMes2));
				retorno.setConsumo2(consumo2 != null ? consumo2.toString()
						: "0");
				retorno.setMesAno3(Util.formatarAnoMesParaMesAno(anoMes3));
				retorno.setConsumo3(consumo3 != null ? consumo3.toString()
						: "0");
				retorno.setMesAno4(Util.formatarAnoMesParaMesAno(anoMes4));
				retorno.setConsumo4(consumo4 != null ? consumo4.toString()
						: "0");
				retorno.setMesAno5(Util.formatarAnoMesParaMesAno(anoMes5));
				retorno.setConsumo5(consumo5 != null ? consumo5.toString()
						: "0");
				retorno.setMesAno6(Util.formatarAnoMesParaMesAno(anoMes6));
				retorno.setConsumo6(consumo6 != null ? consumo6.toString()
						: "0");

				// OBTEM CONSUMO MEDIO
				int idTipoLigacao = getControladorMicromedicao()
						.verificarTipoLigacao(imovel);
				int[] consumoMedio = getControladorMicromedicao()
						.obterVolumeMedioAguaEsgoto(imovel.getId(),
								sistemaParametro.getAnoMesFaturamento(),
								idTipoLigacao);
				retorno.setConsumoMedio(new Integer(consumoMedio[0]).toString());

				// OBTEM DEMOSTRATIVO DO SALDO DA DIVIDA

				// OBTEM VALOR TOTAL
				int indicadorDebito = 1;
				String idImovelPesquisa = imovel.getId().toString();
				String periodoReferenciaDebitoInicial = "000101";
				String periodoReferenciaDebitoFinal = "999912";
				Date periodoVencimentoDebitoInicial = Util
						.converteStringParaDate("01/01/0001");
				Date periodoVencimentoDebitoFinal = Util
						.converteStringParaDate("31/12/9999");

				int indicadorPagamento = 1;
				int indicadorConta = 1;
				int indicadorDebitoACobrar = 1;
				int indicadorCreditoARealizar = 1;
				int indicadorNotasPromissorias = 1;
				int indicadorGuiasPagamento = 1;
				int indicadorAtualizarDebito = 1;

				ObterDebitoImovelOuClienteHelper valorTotalHelper = getControladorCobranca()
						.obterDebitoImovelOuCliente(indicadorDebito,
								idImovelPesquisa, null, null,
								periodoReferenciaDebitoInicial,
								periodoReferenciaDebitoFinal,
								periodoVencimentoDebitoInicial,
								periodoVencimentoDebitoFinal,
								indicadorPagamento, indicadorConta,
								indicadorDebitoACobrar,
								indicadorCreditoARealizar,
								indicadorNotasPromissorias,
								indicadorGuiasPagamento,
								indicadorAtualizarDebito, null);

				BigDecimal valorTotal = getControladorCobranca()
						.calcularValorTotalDebitos(valorTotalHelper);
				retorno.setSaldoTotal(Util.formatarMoedaReal(valorTotal));

				// OBTEM VALOR FATURAS VENCIDAS
				indicadorDebito = 1;
				idImovelPesquisa = imovel.getId().toString();
				periodoReferenciaDebitoInicial = "000101";
				periodoReferenciaDebitoFinal = "999912";
				periodoVencimentoDebitoInicial = Util
						.converteStringParaDate("01/01/0001");
				periodoVencimentoDebitoFinal = new Date();
				indicadorPagamento = 1;
				indicadorConta = 1;
				indicadorDebitoACobrar = 1;
				indicadorCreditoARealizar = 1;
				indicadorNotasPromissorias = 1;
				indicadorGuiasPagamento = 1;
				indicadorAtualizarDebito = 1;

				ObterDebitoImovelOuClienteHelper valorFaturasVencidasHelper = getControladorCobranca()
						.obterDebitoImovelOuCliente(indicadorDebito,
								idImovelPesquisa, null, null,
								periodoReferenciaDebitoInicial,
								periodoReferenciaDebitoFinal,
								periodoVencimentoDebitoInicial,
								periodoVencimentoDebitoFinal,
								indicadorPagamento, indicadorConta,
								indicadorDebitoACobrar,
								indicadorCreditoARealizar,
								indicadorNotasPromissorias,
								indicadorGuiasPagamento,
								indicadorAtualizarDebito, null);

				BigDecimal valorFaturasVencidas = getControladorCobranca()
						.calcularValorContas(valorFaturasVencidasHelper);
				retorno.setFaturasVencidas(Util
						.formatarMoedaReal(valorFaturasVencidas));

				// OBTEM VALOR FATURAS A VENCER
				indicadorDebito = 1;
				idImovelPesquisa = imovel.getId().toString();
				periodoReferenciaDebitoInicial = "000101";
				periodoReferenciaDebitoFinal = "999912";
				periodoVencimentoDebitoInicial = Util
						.adicionarNumeroDiasDeUmaData(new Date(), 1);
				periodoVencimentoDebitoFinal = Util
						.converteStringParaDate("31/12/9999");

				indicadorPagamento = 1;
				indicadorConta = 1;
				indicadorDebitoACobrar = 1;
				indicadorCreditoARealizar = 1;
				indicadorNotasPromissorias = 1;
				indicadorGuiasPagamento = 1;
				indicadorAtualizarDebito = 1;

				ObterDebitoImovelOuClienteHelper valorFaturasAVencerHelper = getControladorCobranca()
						.obterDebitoImovelOuCliente(indicadorDebito,
								idImovelPesquisa, null, null,
								periodoReferenciaDebitoInicial,
								periodoReferenciaDebitoFinal,
								periodoVencimentoDebitoInicial,
								periodoVencimentoDebitoFinal,
								indicadorPagamento, indicadorConta,
								indicadorDebitoACobrar,
								indicadorCreditoARealizar,
								indicadorNotasPromissorias,
								indicadorGuiasPagamento,
								indicadorAtualizarDebito, null);

				BigDecimal valorFaturasAVencer = getControladorCobranca()
						.calcularValorContas(valorFaturasAVencerHelper);
				retorno.setFaturasAVencer(Util
						.formatarMoedaReal(valorFaturasAVencer));

			} else {
				retorno = null;
			}

		} catch (ControladorException ex) {
			throw new FachadaException(ex.getMessage(), ex,
					ex.getParametroMensagem());
		}

		return retorno;
	}

